string name, type, fullname;
Component.GetStrValue("name",name);
ComponentInstance.SetStrValue("name",name);

Udm::Object arch = Component.archetype();
const Uml::Class &PackageClass = arch.GetParent().__impl()->type();
arch.GetStrValue("name", fullname);


Udm::ObjectImpl *oiptr = arch.__impl()->getParent(NULL);
while (oiptr!=&Udm::_null && oiptr->type()==PackageClass)
{ 
  string namepart = oiptr->getStringAttr(Uml::Attribute::meta_name);
  oiptr = oiptr->getParent(NULL);

  fullname = namepart +"/" + fullname;
}


ComponentInstance.SetStrValue("type",fullname);
