        b = (TAO_YY_BUFFER_STATE) tao_yy_flex_alloc( sizeof( struct tao_yy_buffer_state ) );
        if ( ! b )
                TAO_YY_FATAL_ERROR( "out of dynamic memory in tao_yy_create_buffer()" );

