        b->tao_yy_ch_buf[1] = TAO_YY_END_OF_BUFFER_CHAR;

        b->tao_yy_buf_pos = &b->tao_yy_ch_buf[0];

        b->tao_yy_at_bol = 1;
        b->tao_yy_buffer_status = TAO_YY_BUFFER_NEW;

